import React from 'react'
import { ParallaxBanner, withController } from 'react-scroll-parallax'
import { Typography, IconButton, withTheme } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import bg from '../../ressources/img/bg.jpg'
function handleLoad(props) {
    props.parallaxController.update();
}

function Home(props) {
    return (
        <>
            <div style={{
                flex: 1,
                height: '100vh',
                justifyContent: 'center',
                alignItems: 'center',
                alignContent: 'center',
                display: 'flex',
                flexDirection: "row",
            }}>
                <ParallaxBanner
                    onLoad={() => handleLoad(props)}
                    layers={[
                        {
                            children: <div
                                style={{ 
                                    backgroundImage: `url(${bg})`, 
                                    backgroundSize: "auto 100%", 
                                    backgroundPosition: "center center",
                                    backgroundRepeat: "no-repeat",
                                    width: "100%",
                                    height: "100%",
                                    filter: `blur(0.2 * ${window.scrollY}`
                                 }} />,
                            amount: 0.2,

                        },

                        {
                            children: (
                                <div style={{
                                    marginTop: "15%",
                                    marginLeft: "15%",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    width: "80%",
                                }}>
                                    <Typography variant="h1" style={{ fontWeight: 500, color: props.theme.palette.primary.contrastText }}>Pianoservice Kupski</Typography>
                                    <Typography variant="h4" style={{ fontWeight: 500, color: props.theme.palette.primary.contrastText }}>Ihre Ohren werden Augen machen</Typography>
                                </div>),
                            amount: 0.6,
                            expanded: false,
                        },
                        {
                            children: (
                                <div style={{
                                    display: 'flex',
                                    width: "100%",
                                    height: '100%',
                                    alignItems: 'flex-end',
                                    justifyContent: 'center'
                                }}>
                                    <IconButton size="large">
                                        <ExpandMoreIcon fontSize="large" style={{ color: "white" }} />

                                    </IconButton>
                                </div>),
                            amount: 0,
                            expanded: false
                        }
                    ]}
                    style={{
                        height: 'inherit',
                    }}
                    expanded={false}
                />
            </div>
        </>
    )
}

export default withTheme(withController(Home));
