import React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import NoSsr from '@material-ui/core/NoSsr';

const useStyles = theme => ({
    button: {
        "&:hover": {
            color: 'secondary'
        },
        color: 'primary',
    },
    svg: {
        color: 'primary'
    },
    container: {
        justify: 'space-between',
    }
});

const menuTabs = [
    'Über Mich',
    'Leistungen',
    'Klaviere an besonderen Orten',
    'Kontakt'
]
const tabs = {
    home: {
        name: "home",
        color: "primary",
        referenceLink: "",
        svg: 
        <svg
            width="24px"
            height="24px"
            viewBox="0 0 24 24"
            onMouseOver= { (e) => this.handleMouseOver(e)}>
            <path
                fill='#424242'
                color='primary'
                d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
            />
        </svg>
    },
    about: {
        name: "Über mich",
        color: "primary",
        referenceLink: "",        
    },
    services: {
        name: "Leistungen",
        color: "primary",
        referenceLink: "",        
    },
    places: {
        name: "Klaviere an besonderen Orten",
        color: "primary",
        referenceLink: "",
    },
    contact: {
        name: "Kontakt",
        color: "primary",
        referenceLink:"",
    }
}

export default class ButtonBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonColor: 'primary',
            tabs: tabs,
        }
    }
    handleMouseOver = () => {
        var tabs = this.state.tabs;
        tabs.
        this.setState({ tabs: 'secondary' });
    }
    handleMouseLeave = () => {
        this.setState({ buttonColor: 'primary' });
    }
    menuBar(){
        const classes = useStyles();
        for(var tab in this.state.tabs){
            return(
                <Button
                    className={classes}                    
                    color={tab.color}
                    onMouseOver={ (e) => e.target.color = 'secondary'}
                    onMouseLeave={this.handleMouseLeave}
                />
            );
        }
    }
    render() {
        const classes = useStyles();
        return (
            <NoSsr>
                <div>
                    <Grid className={classes.container}>
                        <Button
                            className={classes.button}
                            onMouseOver={ (e) => e.target.color = 'secondary'}
                            >
                            <svg
                                width="24px"
                                height="24px"
                                viewBox="0 0 24 24">
                                <path
                                    fill={this.state.tabs.home.color}
                                    cassName={classes.svg}
                                    d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                                />
                            </svg>
                        </Button>
                        {menuTabs.map(tabName => {
                            return (
                                <Button
                                    className={classes.button}
                                    color={this.state.buttonColor}
                                    onMouseOver={this.handleMouseOver}
                                    onMouseLeave={this.handleMouseLeave}

                                >
                                    {tabName}
                                </Button>
                            )
                        })}

                    </Grid>
                </div>
            </NoSsr>
        );
    }
}
