import React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import NoSsr from '@material-ui/core/NoSsr';

const useStyles = theme => ({
    button: {
        "&:hover": {
            color: 'secondary'
          }
    },
    svg: {
        color: 'primary'
    },
    container: {
        justify: 'space-between',
    }
});

const menuTabs = [
    'Über Mich',
    'Leistungen',
    'Klaviere an besonderen Orten',
    'Kontakt'
]

export default class ButtonBar extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            buttonColor: 'primary',
        }
    }
    handleMouseOver = () => {
        this.setState({buttonColor: 'secondary'});
    }
    handleMouseLeave = () => {
        this.setState({buttonColor: 'primary'});
    }
    render() {
        const classes = useStyles();
        return (
            <NoSsr>
                <div>
                    <Grid className={classes.container}>
                        <Button>
                            <svg
                                width="24px"
                                height="24px"
                                viewBox="0 0 24 24">
                                <path
                                    fill='#424242'
                                    cassName={classes.svg}
                                    d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                                />
                            </svg>
                        </Button>
                        {menuTabs.map(tabName => {
                            return(
                                <Button className={classes.button} color={this.state.buttonColor} onMouseOver={this.handleMouseOver} onMouseLeave={this.handleMouseLeave}>
                                    {tabName}
                                </Button>
                            )
                        })}
                    </Grid>
                </div>
            </NoSsr>
        );
    }
}
