import React from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import NoSsr from '@material-ui/core/NoSsr';
import { withStyles, MenuItem } from '@material-ui/core';
import { BrowserRouter as Router, Link } from 'react-router-dom';

const styles = theme => ({
    container: {
        justify: 'space-between',
        display: 'flex'
    }
});

const menuTabs = [
    'Über Mich',
    'Leistungen',
    'Klaviere an besonderen Orten',
    'Kontakt'
]
const tabs = {
    home: {
        name: "home",
        color: "primary",
        path: "/",
        svg:<svg
                width="24px"
                height="24px"
                viewBox="0 0 24 24"
            >
                <path
                    fill='#424242'
                    color='primary'
                    d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                />
            </svg>
    },
    about: {
        name: "Über mich",
        color: "primary",
        path: "/about",
    },
    services: {
        name: "Leistungen",
        color: "primary",
        path: "/services",
    },
    places: {
        name: "Klaviere an besonderen Orten",
        color: "primary",
        path: "/special-places",
    },
    contact: {
        name: "Kontakt",
        color: "primary",
        path: "/contact",
    }
}

class ButtonBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonColor: 'primary',
            tabs: tabs,
        }
    }
    handleClick = (nav) => {
        window.location.href = nav;
    }

    handleMouseOver = (tab) => {
        var tabs = this.state.tabs;
        tab.color = 'secondary';
        for (var t in tabs) {
            if (tab === t) {
                tabs.t = tab;
            }
        }
        this.setState({ tabs: tabs });
    }
    handleMouseLeave = () => {
        this.setState({ buttonColor: 'primary' });
    }
    menuBar() {
        for (var tab in this.state.tabs) {
            return (
                <Button
                    color={tab.color}
                    onClick={() => this.handleClick(tabs.home.path)}
                    onMouseOver={(e) => e.target.color = 'secondary'}
                    onMouseLeave={this.handleMouseLeave}
                />
            );
        }
    }
    render() {
        const { classes } = this.props;
        return (
            <NoSsr>
                <Router>
                    <Grid className={classes.container}>
                        <MenuItem
                            component={Link}
                            to={tabs.home.path}
                            color={this.state.tabs.home.color}
                            onClick={() => this.handleClick(tabs.home.path)}
                            onMouseOver={(e) => e.target.color = 'secondary'}
                        >
                            {tabs.home.svg}
                        </MenuItem>

                        <MenuItem
                            component={Link}
                            to={tabs.about.path}
                            onClick={() => this.handleClick(tabs.about.path)}
                            color={this.state.tabs.about.color}
                            onMouseOver={(tab) => this.handleMouseOver(tab)}
                            onMouseLeave={this.handleMouseLeave}
                        >
                            {tabs.about.name}
                        </MenuItem>

                        <MenuItem
                            component={Link}
                            to={tabs.services.path}
                            onClick={() => this.handleClick(tabs.services.path)}
                            color={this.state.tabs.services.color}
                            onMouseOver={(tab) => this.handleMouseOver(tab)}
                            onMouseLeave={this.handleMouseLeave}
                        >
                            {tabs.services.name}
                        </MenuItem>

                        <MenuItem
                            component={Link}
                            to={tabs.places.path}
                            onClick={() => this.handleClick(tabs.places.path)}
                            color={this.state.tabs.places.color}
                            onMouseOver={(tab) => this.handleMouseOver(tab)}
                            onMouseLeave={this.handleMouseLeave}
                        >
                            {tabs.places.name}
                        </MenuItem>

                        <MenuItem
                            component={Link}
                            to={tabs.contact.path}
                            onClick={() => this.handleClick(tabs.contact.path)}
                            color={this.state.tabs.contact.color}
                            onMouseOver={(tab) => this.handleMouseOver(tab)}
                            onMouseLeave={this.handleMouseLeave}
                        >
                            {tabs.contact.name}
                        </MenuItem>

                    </Grid>
                </Router>
            </NoSsr>
        );
    }
}

ButtonBar.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ButtonBar);
