import React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import NoSsr from '@material-ui/core/NoSsr';
import { withStyles, withTheme, MenuItem } from '@material-ui/core';
import { BrowserRouter as Router, Link } from 'react-router-dom';

const styles = theme => ({
    container: {
        justify: 'space-between',
        display: 'flex'
    }
});

class ButtonBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tabs: {
                home: {
                    name: "Home",
                    color: props.theme.palette.secondary.main,
                    path: "/",
                    svg: (props) => 
                    {return (
                        <svg
                            width="24px"
                            height="24px"
                            viewBox="0 0 24 24"
                        >
                            <path
                                fill={this.state.tabs ? this.state.tabs.home.color : this.props.theme.palette.primary.main}
                                d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                            />
                        </svg>
                    )}
                    },
                    about: {
                        name: "Über mich",
                        color: props.theme.palette.secondary.main,
                        path: "/about",
                    },
                    services: {
                        name: "Leistungen",
                        color: "primary",
                        path: "/services",
                    },
                    places: {
                        name: "Klaviere an besonderen Orten",
                        color: "primary",
                        path: "/special-places",
                    },
                    contact: {
                        name: "Kontakt",
                        color: "primary",
                        path: "/contact",
                    }
                },
            }
        this.theme = props.theme;

        }
        handleClick = (nav) => {
            window.location.href = nav;
        }

        handleMouseOver = (component) => {
        }
        handleMouseLeave = (component) => {

        }
        render() {
            const { classes } = this.props;
            return (
                <NoSsr>
                    <Router>
                        <Grid className={classes.container}>
                            <MenuItem
                                ref={React.createRef()}
                                component={props => <Button {...props} component={Link} style={{ color: this.state.tabs.home.color }}></Button>}
                                to={this.state.tabs.home.path}
                                onClick={() => this.handleClick(this.state.tabs.home.path)}
                            >
                                {this.state.tabs.home.svg}
                            </MenuItem>

                            <MenuItem
                                component={props => <Button {...props}
                                    component={Link}
                                    style={{ color: this.state.tabs.about.color, textTransform: 'none' }} />}
                                to={this.state.tabs.about.path}
                                onClick={() => this.handleClick(this.state.tabs.about.path)}
                            >
                                {this.state.tabs.about.name}
                            </MenuItem>

                            <MenuItem
                                component={Link}
                                to={this.state.tabs.services.path}
                                onClick={() => this.handleClick(this.state.tabs.services.path)}
                                color={this.state.tabs.services.color}
                            >
                                {this.state.tabs.services.name}
                            </MenuItem>

                            <MenuItem
                                component={Link}
                                to={this.state.tabs.places.path}
                                onClick={() => this.handleClick(this.state.tabs.places.path)}
                                color={this.state.tabs.places.color}
                            >
                                {this.state.tabs.places.name}
                            </MenuItem>

                            <MenuItem
                                component={Link}
                                to={this.state.tabs.contact.path}
                                onClick={() => this.handleClick(this.state.tabs.contact.path)}
                                color={this.state.tabs.contact.color}
                            >
                                {this.state.tabs.contact.name}
                            </MenuItem>

                        </Grid>
                    </Router>
                </NoSsr>
            );
        }
    }

    export default withTheme(withStyles(styles)(ButtonBar));
