import React from 'react';
import { withStyles, createMuiTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import { styled, ThemeProvider } from '@material-ui/styles';
import { darken, fade } from '@material-ui/core/styles';
import NoSsr from '@material-ui/core/NoSsr';

const useStyles = theme => ({
    button: {
        "&:hover": {
            color: 'secondary'
          }
    },
    svg: {
        color: 'primary'
    },
    container: {
        justify: 'space-between',
    }
});

const menuTabs = [
    'Über Mich',
    'Leistungen',
    'Klaviere an besonderen Orten',
    'Kontakt'
]
const StyledButton = styled(Button)`
  background: linear-gradient(45deg, #fe6b8b 30%, #ff8e53 90%);
  border-radius: 3px;
  border: 0;
  color: white;
  height: 48px;
  padding: 0 30px;
  box-shadow: 0 3px 5px 2px rgba(255, 105, 135, 0.3);
`;


export default class ButtonBar extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            mouseover: false,
            color: '#424242'
        }
    }

    render() {
        const classes = useStyles();
        return (
            <NoSsr>
                <div>
                    <Grid className={classes.container}>
                        <StyledButton>
                            <svg
                                width="24px"
                                height="24px"
                                viewBox="0 0 24 24">
                                <path
                                    fill='#424242'
                                    cassName={classes.svg}
                                    d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                                />
                            </svg>
                        </StyledButton>
                        {menuTabs.map(tabName => {
                            return(
                                <StyledButton>
                                    {tabName}
                                </StyledButton>
                            )
                        })}
                    </Grid>
                </div>
            </NoSsr>
        );
    }
}
