import React from 'react';
import NavigationBar from './components/menubar/NavigationBar'
import { createMuiTheme, ThemeProvider, responsiveFontSizes } from '@material-ui/core/styles';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import NoMatch from "./components/sites/NoMatch";
import Home from "./components/sites/Home"
import { CssBaseline } from '@material-ui/core';
import { ParallaxProvider } from 'react-scroll-parallax';
import About from './components/sites/About';
import Services from './components/sites/Services';
import SpecialPlaces from './components/sites/SpecialPlaces';
import Contact from './components/sites/Contact';
import Footer from './components/menubar/Footer';
import Impressum from './components/sites/Impressum'


var theme = createMuiTheme({
  palette: {
    primary: {
      main: '#424242',
      dark: '#1b1b1b',
      light: '#6d6d6d',
      contrastText: '#ffffff',
    },
    secondary: {
      light: '#ff5f52',
      main: '#c62828',
      dark: '#8e0000',
      contrastText: '#ffffff',
    },
    // error: will use the default color
  },
  typography: {
    fontFamily: [
      'Big John PRO',
    ].join(','),
  },

});
theme = responsiveFontSizes(theme);



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      initializing: true,
      width: undefined,
      mobileView: false,
    }
  }

  updateWidth = () => {
    console.log(window.innerWidth)
    const width = window.innerWidth
    var mobileView = false;
    if (width < 850) {
      mobileView = true;
    }
    this.setState({
      width: width,
      mobileView: mobileView
    });
  }
  scroll= () => {
    //console.log(window.scrollY);
  }

  componentDidMount() {
    window.addEventListener('resize', () => this.updateWidth());
    window.addEventListener('scroll', () => this.scroll());
    this.setState({
      initializing: false,
      width: window.innerWidth
    });
    this.updateWidth();
  }
  componentWillUnmount() {
    window.removeEventListener('resize', () => this.updateWidth());
  }

  navBar() {
    if(window.scrollY > window.innerHeight){

    }else {

    }
  }

  render() {
    return (
      <React.Fragment>
        <ParallaxProvider>
          <ThemeProvider theme={theme} >
            <CssBaseline />
            <NavigationBar mobile={this.state.mobileView} />
            <Router>
              <Switch>
                <Route exact path="/" component={Home} />
                <Route path="/about" component={About} />
                <Route path="/services" component={Services} />
                <Route path="/special_places" component={SpecialPlaces} />
                <Route path="/contact" component={Contact} />
                <Route path="/impressum" component={Impressum} />
                <Route component={NoMatch} />
              </Switch>
            </Router>
            <Footer/>
          </ThemeProvider>
        </ParallaxProvider>
      </React.Fragment>
    );
  }
}

export default App;
