import React from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import NoSsr from '@material-ui/core/NoSsr';
import { withStyles, MenuItem } from '@material-ui/core';
import { Router } from 'react-router';

const styles = theme => ({
    container: {
        justify: 'space-between',
    }
});

const menuTabs = [
    'Über Mich',
    'Leistungen',
    'Klaviere an besonderen Orten',
    'Kontakt'
]
const tabs = {
    home: {
        name: "home",
        color: "primary",
        path: "/",
        svg: 
        <svg
            width="24px"
            height="24px"
            viewBox="0 0 24 24"
            onMouseOver= { (e) => this.handleMouseOver(e)}>
            <path
                fill='#424242'
                color='primary'
                d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
            />
        </svg>
    },
    about: {
        name: "Über mich",
        color: "primary",
        path: "/about",        
    },
    services: {
        name: "Leistungen",
        color: "primary",
        path: "/services",        
    },
    places: {
        name: "Klaviere an besonderen Orten",
        color: "primary",
        path: "",
    },
    contact: {
        name: "Kontakt",
        color: "primary",
        path:"/contact",
    }
}

class ButtonBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            buttonColor: 'primary',
            tabs: tabs,
        }
    }
    handleClick = (nav) => {
        window.location.href = nav;
    }

    handleMouseOver = (tab) => {
        var tabs = this.state.tabs;
        tab.color = 'secondary';
        for(var t in tabs){
            if(tab === t){
                tabs.t = tab;
            }
        }
        this.setState({ tabs: tabs });
    }
    handleMouseLeave = () => {
        this.setState({ buttonColor: 'primary' });
    }
    menuBar(){
        for(var tab in this.state.tabs){
            return(
                <Button                 
                    color={tab.color}
                    onMouseOver={ (e) => e.target.color = 'secondary'}
                    onMouseLeave={this.handleMouseLeave}
                />
            );
        }
    }
    render() {
        const {classes} = this.props;
        return (
            <NoSsr>
                <Router>
                    <Grid className={classes.container}>
                        <MenuItem
                            onMouseOver={ (e) => e.target.color = 'secondary'}
                            >
                            <svg
                                width="24px"
                                height="24px"
                                viewBox="0 0 24 24">
                                <path
                                    d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                                />
                            </svg>
                        </MenuItem>
                        {menuTabs.map(tabName => {
                            return (
                                <MenuItem
                                    component={Link}
                                    to={tabName.path}
                                    color={this.state.buttonColor}
                                    onMouseOver={ (tab) => this.handleMouseOver(tab)}
                                    onMouseLeave={this.handleMouseLeave}

                                >
                                    {tabName}
                                </MenuItem>
                            )
                        })}

                    </Grid>
                </Router>
            </NoSsr>
        );
    }
}

ButtonBar.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ButtonBar);
