import React from 'react';
import {  withStyles, createMuiTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid'
import  styled, { ThemeProvider } from '@material-ui/styles';
import { darken, fade } from '@material-ui/core/styles';
import NoSsr from '@material-ui/core/NoSsr';

const styles = theme => ({
    button: {
        color: 'primary'
    },
    svg: {
        color: 'primary'
    },
    container: {
        justify: 'space-between',
    }
});

const defaultTheme = createMuiTheme();

const menuTabs = [
    'Über Mich',
    'Leistungen',
    'Klaviere an besonderen Orten',
    'Kontakt'
]

const StyledButton = styled.button`
${({ theme }) => `
  padding: 8px 12px;
  border: 1px solid;
  cursor: pointer;
  outline: none;
  border-radius: ${theme.shape.borderRadius}px;
  color: ${theme.palette.primary.contrastText};
  background-color: ${theme.palette.primary.main};
  border-color: ${theme.palette.primary.main};
  transition: ${theme.transitions.create(['background-color', 'box-shadow'])};
  font-family: ${[
    '-apple-system',
    'BlinkMacSystemFont',
    '"Segoe UI"',
    'Roboto',
    '"Helvetica Neue"',
    'Arial',
    'sans-serif',
    '"Apple Color Emoji"',
    '"Segoe UI Emoji"',
    '"Segoe UI Symbol"',
  ].join(',')};
  &:hover {
    background-color: ${darken(theme.palette.primary.main, 0.1)};
    border-color: ${darken(theme.palette.primary.main, 0.2)};
  }
  &:active {
    background-color: ${darken(theme.palette.primary.main, 0.2)};
    border-color: ${darken(theme.palette.primary.main, 0.3)};
  }
  &:focus {
    box-shadow: 0 0 0 0.2rem ${fade(theme.palette.primary.main, 0.5)};
  }
  font-size: 18px;
  ${theme.breakpoints.up('md')} {
    font-size: 16px;
  }
  `}
`

class CustomButton extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            mouseover: false,
            color: '#424242'
        }
    }

    render() {
        const classes = this.props;
        return (
            <div>
                <NoSsr>
                <ThemeProvider theme={defaultTheme}>
                <Grid className={classes.container}>
                    <StyledButton>
                        <svg
                            width="24px"
                            height="24px"
                            viewBox="0 0 24 24">
                            <path
                                fill='#424242'
                                cassName={classes.svg}
                                d="M12 5.69L17 10.19V18H15V12H9V18H7V10.19L12 5.69M12 3L2 12H5V20H11V14H13V20H19V12H22L12 3Z"
                            />
                        </svg>
                    </StyledButton>
                    <StyledButton color="inherit">Über Mich</StyledButton>
                    <StyledButton color="inherit">Leistungen</StyledButton>
                    <StyledButton color="inherit">Klaviere an Besonderen Orten</StyledButton>
                    <StyledButton color="inherit">Referenzen</StyledButton>
                    <StyledButton color="inherit">Kontakt</StyledButton>
                </Grid>
                </ThemeProvider>
                </NoSsr>
            </div>

        );
    }
}

export default withStyles(styles)(CustomButton);
